import useFetch from "../useFetch";

const Admin = () => {

    var data, isPending, error = useFetch("http://localhost:5000/countries");

    return <div>
        <h2>Admin Page</h2>
        {isPending && <div><br />Loading ...<br /><br /></div>}
        {error && <div><br />{ error }<br /><br /></div>}
        {/* Only load the elements when the data is loaded */}
        {data &&
        <div>
            {data.map((element, key) =>
                    <form action="http://localhost:5000/countries" method="DELETE">
                        <label htmlFor="name"></label>
                    </form>
            )}

            <form action="http://localhost:5000/countries" method="POST">
                <label htmlFor="name">Name:</label>
                <input type="text" id="name" name="name" required></input>
                <button>Submit</button>
            </form>
        </div>
        }
    </div>;
}

export default Admin;
